import {drgns} from './Body';

// Filter by Trainable
export const TFilter = (op) => {
    fdrgns = drgns.filter(drgn => {
        return drgn.trainable === true ? op === 'Yes' : drgn.trainable === false;
    });

    return map(drgns.name);
}
